mapper.insert:

	select NVL(MAX(user_idx), 0) as userIdx from user_info 


INSERT INTO USER_INFO
	(
	USER_IDX

	, USER_NAME
	, USER_SOCIAL_SECUNUM
	, USER_SEX
	, USER_COMP
	, USER_COMP_ENTERDATE
	, USER_DEPT
	, USER_SPOT
	, USER_ARMY_SERV_PERIOD
	, USER_MARITAL_STATUS
	, USER_ARMY_SERV_ENTER
	, USER_ARMY_SERV_LEAVE
	, USER_ARMY_SERV
	, USER_TELNUM_WIRED
	, USER_TELNUM_WIRELESS
	, USER_EMAIL
	, USER_ZIPCODE
	, USER_ADDRESS
	)
VALUES
	(
	USER_INFO_SEQ.NEXTVAL

	, #{userName}
	, #{userSocialSecunum}
	, #{userSex}
	, #{userComp}
	, #{userCompEnterdate}
	, #{userDept}
	, #{userSpot}
	, #{userArmyServPeriod}
	, #{userMaritalStatus}
	, #{userArmyServEnter}
	, #{userArmyServLeave}
	, #{userArmyServ}
	, #{userTelnumWired}
	, #{userTelnumWireless}
	, #{userEmail}
	, #{userZipcode}
	, #{userAddress}
	)

mapper.update:
UPDATE
	USER_INFO
SET
	  USER_NAME = #{userName}
	, USER_SOCIAL_SECUNUM = #{userSocialSecunum}
	, USER_SEX = #{userSex}
	, USER_COMP = #{userComp}
	, USER_COMP_ENTERDATE = #{userCompEnterdate}
	, USER_DEPT = #{userDept}
	, USER_SPOT = #{userSpot}
	, USER_ARMY_SERV_PERIOD = #{userArmyServPeriod}
	, USER_MARITAL_STATUS = #{userMaritalStatus}
	, USER_ARMY_SERV_ENTER = #{userArmyServEnter}
	, USER_ARMY_SERV_LEAVE = #{userArmyServLeave}
	, USER_ARMY_SERV = #{userArmyServ}
	, USER_TELNUM_WIRED = #{userTelnumWired}
	, USER_TELNUM_WIRELESS = #{userTelnumWireless}
	, USER_EMAIL = #{userEmail}
	, USER_ZIPCODE = #{userZipcode}
	, USER_ADDRESS = #{userAddress}
WHERE
	USER_IDX = #{userIdx}
